<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head>	<title>Depuração do Erro</title>	<meta http-equiv="Content-Type" content="text/html; charset=<?php echo charset ?>">	<style type="text/css">		body		{			font-family: Arial;			font-size: 14px;			margin: 0;			background: #FFF;			color: #333;			margin: 40px;		}		p		{			margin: 0;			padding: 0;		}		h1 		{			margin: 0;			margin-bottom: 5px;			padding: 0;			font-size: 30px;			text-transform: uppercase;		}		h2		{			margin: 0;			font-size: 1.2em;			margin-bottom: 5px;			margin-top: 10px;		}		.header		{			padding: 15px;			padding-left: 0px;			border-bottom: 5px solid #FFA824;					}		.code pre, .code .pre		{			background: #FcFcFc;			font-family: monospace;			font-size: 1.2em;			padding: 15px;			border: solid 1px #DEDEDE;			margin: 0;			overflow-y: auto;		}		.server		{			margin: 0px;			overflow: hidden;		}		.server table		{			border-collapse: collapse;			width: 100%;			font-size: 0.8em;		}		.server th		{			text-align: left;			padding: 5px;		}		.server td 		{			padding: 5px;			border-top: solid 1px #DDD;			border-bottom: solid 1px #DDD;		}		.server tr:nth-child(odd) td		{			background: #F9F9F9;		}		.server .name		{			width: 220px;		}		.server .n		{			width: 40px;		}		.toggle		{			float: right;			font-size: 0.7em;		}		.environment		{			color: #000;			padding: 15px 0px;				border-bottom: 3px solid #104E8B;				}		.environment h1		{			font-size: 24px;			margin-bottom: 3px;			color: #222;		}		.environment p		{			font-size: 12px;		}	</style></head><body>	<div class="header">		<h1>Depuração do Erro</h1>		<p><b>Mensagem:</b> <?php echo $error->getMessage() ?></p>	</div>	<div class="code">		<h2>Correção:</h2>		<div class="pre"><?php echo method_exists($error, 'getDetails') ? $error->getDetails() : '' ?>		</div>		<h2>Arquivo:</h2>		<pre><?php echo $error->getFile() ?>(<?php echo $error->getLine() ?>)</pre>		<h2>Rastreamento:</h2>		<pre><?php echo $error->getTraceAsString() ?></pre>	</div>	<div class="environment">		<h1>Ambiente</h1>		<p>Variáveis de ambiente</p>	</div>	<div class="variables">		<h2>Server: <a href="javascript:;" onclick="toggle('server', this)" class="toggle">[expandir]</a></h2>		<div class="server" id="server" style="height:100px">			<table>				<thead>					<tr>						<th>#</th>						<th>Nome</th>						<th>Valor</th>					</tr>				</thead>				<tbody>					<?php $i = 0 ?>					<?php foreach($_SERVER as $k => $v): ?>					<tr>						<td class="n"><?php echo ++$i ?></td>						<td class="name"><?php echo $k ?></td>						<td><?php echo $v ?></td>					</tr>					<?php endforeach ?>				</tbody>			</table>		</div>		<h2>Constantes: <a href="javascript:;" onclick="toggle('constants', this)" class="toggle">[expandir]</a></h2>		<div class="server" id="constants" style="height:100px">			<table>				<thead>					<tr>						<th>#</th>						<th>Nome</th>						<th>Valor</th>					</tr>				</thead>				<tbody>					<?php $i = 0 ?>					<?php $constants = get_defined_constants(true) ?>					<?php foreach($constants['user'] as $k => $v): ?>					<tr>						<td class="n"><?php echo ++$i ?></td>						<td class="name"><?php echo $k ?></td>						<?php if($v === null): ?>							<td>null</td>						<?php elseif($v === true): ?>							<td>true</td>						<?php elseif($v === false): ?>							<td>false</td>						<?php elseif($v === 0): ?>							<td>0</td>						<?php elseif($v === "\r\n"): ?>							<td>\r\n</td>						<?php else: ?>							<td><?php echo htmlentities($v) ?></td>						<?php endif ?>					</tr>					<?php endforeach ?>				</tbody>			</table>		</div>		<h2>String SQL: <a href="javascript:;" onclick="toggle('debug', this)" class="toggle">[expandir]</a></h2>		<div class="server" id="debug" style="height:100px">			<table>				<thead>					<tr>						<th>#</th>						<th>SQL</th>					</tr>				</thead>				<tbody>					<?php $i = 0 ?>					<?php foreach(Debug::all() as $v): ?>					<tr>						<td class="n"><?php echo ++$i ?></td>						<td><?php echo $v ?></td>					</tr>					<?php endforeach ?>				</tbody>			</table>		</div>	</div>	<script type="text/javascript">		var toggle = function(id, t) {			var e = document.getElementById(id);			if(e.style.height == '100px') {				e.style.height = '';				t.innerHTML = '[minimizar]';			} else {				e.style.height =  '100px';				t.innerHTML = '[expandir]';			}		};	</script></body></html>