abled() && !IS_AJAX && !IS_AUTODOT)		{			foreach(self::$debugs as $d)				echo $d . BR . NL;		}	}		/**	 * Retorna todas as linhas de depuraçãob_clean	 * @return	array	retorna a lista com os debugs	 */	public static function all()	{		return self::$debugs;	}		/**	 * Verifica se o usuário está acessando via rede	 * @param	string	$ip	IP do usuário	 * @return	boolean		retorna verdadeiro se o usuário estiver acessando pela rede, no contrário retorna falso	 */	private static function isNetwork($ip)	{		return false;	}		/**	 * Verifica se o debug está habilidade para este usuário	 * @return	boolean		retorna verdadeiro se o debug estiver habilitado	 */	public static function enabled()	{		if(Config::get('debug') != 'off')		{			if(Config::get('debug') == 'local' && (IP == '127.0.0.1' || IP == '::1'))				return true;			if(Config::get('debug') == 'all')				return true;			if(Config::get('debug') == 'network' && self::isNetwork(IP))				return true;		}		return false;	}}